import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import{ useParams} from 'react-router-dom'
import {RemoveCountry} from '../redux/actions/country'
import { AppState } from '../types'


type Id = {
    name:string
  } 

export default function Cart() {
  const { name } = useParams<Id>()

  const dispatch= useDispatch()

  const { inCart } = useSelector((state: AppState) => state.country)
  return (
    <div style={{ padding: '10px' }} className="cart-container">
      <h2>Items: </h2>
      {inCart.length <= 0 && <div>No product in cart</div>}
      {inCart.map((country) => {
        return (
          <ul className="item-list" key={country.name}>
            <li>
              <img className="flag" src={country.flag} alt="flag" />
            </li>
            <li>{country.name}</li>
            <li>
              <button

                color="primary"
                onClick={() => dispatch(RemoveCountry(country))}
              >
                Remove
              </button>
            </li>
          </ul>
        )
      })}
    </div>
  )
}
