import {CountryState, SET_COUNTRIES_LIST, CountryAction, ADD_COUNTRY} from '../../types'


export default function country (
    state: CountryState = {
        allCountries: [], 
        inCart:[]
    },
    action: CountryAction 
): CountryState {
    switch (action.type) {

    case ADD_COUNTRY:{
        const {country} = action.payload
        console.log(country)
        return state
    }

    case SET_COUNTRIES_LIST: {
        return{...state, allCountries: action.payload.country}
    }

    default:
        return  state
    }
}
