import { Dispatch } from 'redux'

import {
  ADD_PRODUCT,
  REMOVE_PRODUCT,
  // SET_PRODUCTS,
  // SELECTED_PRODUCTS,
  ProductActions,
  Product,
} from '../../types'
// import product from '../reducers/product'

export function addProduct(product: Product): ProductActions {
  return {
    type: ADD_PRODUCT,
    payload: {
      product,
    },
  }
}

export function removeProduct(product: Product): ProductActions {
  return {
    type: REMOVE_PRODUCT,
    payload: {
      product,
    },
  }
}

// export const setProducts = (product: Product) => {
//   return{
//     type: SET_PRODUCTS,
//     payload: product,
//   }
// }

// export const seletedProducts = (product: Product) => {
//   return{
//     type: SELECTED_PRODUCTS,
//     payload: product,
//   }
// }
// Async action processed by redux-thunk middleware
export function fetchProduct(productId: string) {
  return (dispatch: Dispatch) => {
    return fetch(`products/${productId}`)
      .then((resp) => resp.json())
      .then((product) => {
        dispatch(addProduct(product))
      })
  }
}
