// Action types
export const SET_COUNTRIES_LIST = 'SET_COUNTRIES_LIST '
export const ADD_COUNTRY = 'ADD_COUNTRY'
export const REMOVE_COUNTRY = 'REMOVE_COUNTRY'
export const ADD_INCREMENT = 'ADD_INCREMENT'
export const ADD_PRODUCT = 'ADD_PRODUCT'
export const REMOVE_PRODUCT = 'REMOVE_PRODUCT'

// export const SET_COUNTRIES_SUCCESS = 'SET_COUNTRIES_SUCCESS '


// export type SetCountriesSuccess ={
//   type: typeof SET_COUNTRIES_SUCCESS
//   payload: Country[]
// }



// A product
export type Product = {
  id: string
  name: string
  price: number
}
export type AddProductAction = {
  type: typeof ADD_PRODUCT
  payload: {
    product: Product
  }
}


export type RemoveProductAction = {
  type: typeof REMOVE_PRODUCT
  payload: {
    product: Product
  }
}

export type AddCounterAction = {
  type: typeof ADD_INCREMENT
}
export type CounterState = {
  count: number,
}

//Country
export type Country = {
  name: string
  population: number
  flag: string
  region: string
  language: string
  nativeName?: string
}

export type SetCountryListAction = {
  type: typeof SET_COUNTRIES_LIST
  payload: {
    country: Country[]
  }
}


export type AddCountryAction = {
  type: typeof ADD_COUNTRY
  payload: {
    country: Country
  }
}

export type RemoveCountryAction = {
  type: typeof REMOVE_COUNTRY
  payload: {
    country: Country
  }
}

// Use this union in reducer
export type ProductActions = AddProductAction | RemoveProductAction
export type ProductState = {
  inCart: Product[]
}
export type CountryAction = SetCountryListAction | AddCountryAction | RemoveCountryAction

export type CountryState = {
  allCountries: Country[]
  inCart: Country[]
}


export type AppState = {
  product: ProductState
  counter: CounterState
  country: CountryState
}
