import {CountryState, SET_COUNTRIES_LIST, CountryAction} from '../../types'

export default function country (
    state: CountryState = {
        allCountries: [], 
        inCart:[]
    },
    action: CountryAction 
): CountryState {
    switch (action.type) {
    case SET_COUNTRIES_LIST: {
        return{...state, allCountries: action.payload.country}
    }

    default:
        return  state
    }
}
